from flask import Flask, render_template
from classes import*
app = Flask(__name__)

@app.route('/')
def index():
	proteins = Product( "Протеины", "/proteins/", "/static/imgs/protein.jpg" )
	gainers = Product( "Гейнеры", "/gainers/", "/static/imgs/gainers.jpg")		
	aminoacids = Product( "Аминокислоты", "/aminoacids/", "/static/imgs/aminoacids.jpg")
	creatine = Product( "Креатин", "/creatine/", "/static/imgs/creatine.jpg")
	burnfats = Product( "Жиросжигатели", "/burnfats/", "/static/imgs/burnfat.jpg")
	vitamins = Product( "Витамины", "/vitamins/", "/static/imgs/vita.jpg")
	steroids = Product( "Стероиды", "/steroids/", "/static/imgs/steroids.jpg")

	products = [ proteins, gainers, aminoacids, creatine, burnfats, vitamins, steroids]

	home = NavItem( "Домашняя страница", "/")
	product_list = NavItem( "Список товаров сайта", "/productlist/")
	sertification = NavItem( "Сертификаты продукции", "/certificates/")
	about_us = NavItem( "О нас", "/aboutus/")

	nav = Navigation(0)

	nav.push(home)
	nav.push(product_list)
	nav.push(sertification)
	nav.push(about_us)
	return render_template('index.html', products=products, nav=nav)

